                    String key = noEffectIterator.next();
                    filterMakePhotoModelArray[i].setNoEffectParams(key, noEffectParamMap.get(key));
                }

                isStartToEndNull = false;
            }
        }

        if (isStartToEndNull) {
            return null;
        } else {
            return filterMakePhotoModelArray;
        }
