            GLogger.e(TAG, e);
        } finally {
            if (null != cursor)
                cursor.close();
            if (null != db && db.isOpen())
                db.close();
        }

        // init ce in ces
        for ( CompositeEffect ce : ces ) {
            ce.init();
        }

        return ces;
    }

